import React from 'react';
import { Select } from './ui/Select';
import { Button } from './ui/Button';

const CryptoSelector = ({ coin, setCoin, fetchStats, fetchDeviation, loading }) => {
  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6 mb-8">
      <h2 className="text-2xl font-semibold mb-4">Select Cryptocurrency</h2>
      <div className="flex flex-col sm:flex-row gap-4">
        <Select
          value={coin}
          onChange={(e) => setCoin(e.target.value)}
          options={[
            { value: 'bitcoin', label: 'Bitcoin' },
            { value: 'matic-network', label: 'Matic' },
            { value: 'ethereum', label: 'Ethereum' },
          ]}
        />
        <Button onClick={fetchStats} disabled={loading}>
          Get Stats
        </Button>
        <Button onClick={fetchDeviation} disabled={loading}>
          Get Deviation
        </Button>
      </div>
    </div>
  );
};

export default CryptoSelector;
