import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:8000';

function App() {
  const [coin, setCoin] = useState('bitcoin');
  const [stats, setStats] = useState(null);
  const [deviation, setDeviation] = useState(null);
  const [error, setError] = useState(null);

  const fetchStats = async () => {
    try {
      const response = await axios.get(`${API_URL}/stats?coin=${coin}`);
      setStats(response.data);
      setError(null);
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred');
      setStats(null);
    }
  };

  const fetchDeviation = async () => {
    try {
      const response = await axios.get(`${API_URL}/deviation?coin=${coin}`);
      setDeviation(response.data.deviation);
      setError(null);
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred');
      setDeviation(null);
    }
  };

  return (
    <div className="App">
      <h1>Crypto Stats</h1>
      <select value={coin} onChange={(e) => setCoin(e.target.value)}>
        <option value="bitcoin">Bitcoin</option>
        <option value="matic-network">Matic</option>
        <option value="ethereum">Ethereum</option>
      </select>
      <button onClick={fetchStats}>Get Stats</button>
      <button onClick={fetchDeviation}>Get Deviation</button>
      {error && <p className="error">{error}</p>}
      {stats && (
        <div>
          <h2>Stats</h2>
          <p>Price: ${stats.price}</p>
          <p>Market Cap: ${stats.marketCap}</p>
          <p>24h Change: {stats['24hChange']}%</p>
        </div>
      )}
      {deviation && (
        <div>
          <h2>Standard Deviation</h2>
          <p>{deviation}</p>
        </div>
      )}
    </div>
  );
}

export default App;
