import React, { useState } from 'react';
import { ThemeProvider } from './components/ThemeProvider';
import Header from './components/Header';
import CryptoSelector from './components/CryptoSelector';
import StatsCard from './components/StatsCard';
import DeviationCard from './components/DeviationCard';
import { Toast } from './components/ui/Toast';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

function App() {
  const [coin, setCoin] = useState('bitcoin');
  const [stats, setStats] = useState(null);
  const [deviation, setDeviation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchStats = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_URL}/stats?coin=${coin}`);
      const data = await response.json();
      if (response.ok) {
        setStats(data);
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setError(error.message);
      setStats(null);
    } finally {
      setLoading(false);
    }
  };

  const fetchDeviation = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_URL}/deviation?coin=${coin}`);
      const data = await response.json();
      if (response.ok) {
        setDeviation(data.deviation);
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setError(error.message);
      setDeviation(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider>
      <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
        <div className="container mx-auto px-4 py-8">
          <Header />
          <main>
            <CryptoSelector
              coin={coin}
              setCoin={setCoin}
              fetchStats={fetchStats}
              fetchDeviation={fetchDeviation}
              loading={loading}
            />
            {stats && <StatsCard stats={stats} coin={coin} />}
            {deviation !== null && <DeviationCard deviation={deviation} coin={coin} />}
          </main>
        </div>
        {error && <Toast message={error} type="error" />}
      </div>
    </ThemeProvider>
  );
}

export default App;
